import React from 'react';
import logo from './logo.svg';
import './App.css';

class LoginForm extends React.Component{

  constructor() {
    super();
    this.state = {
      selectedOption: 'Email'
    };
  this.radioChange = this.radioChange.bind(this);
}

radioChange(e) {
  this.setState({
    selectedOption: e.currentTarget.value
  });
}

  render(){
    return(
      <div id="loginform">
        <FormHeader title="Sample Form" />
        <div>
     <FormInput description="Name" placeholder="Enter  Name" type="text" />

    <div style={{color:'rgba(187,187,187,0.9)',paddingTop:'2rem',textAlign: 'left',
    paddingLeft: '4.5rem',
    paddingBottom: '0.5rem'}} >Search By</div>
     <div style={{display:'flex',color:'black',justifyContent:'space-around'}}>
        <div>
        <input type="radio"
               value="Email"
               checked={this.state.selectedOption === "Email"}
               onChange={this.radioChange} />
               Email
               </div>
               <div>
        <input type="radio"
               value="Phone"
               checked={this.state.selectedOption === "Phone"}
               onChange={this.radioChange}/>Phone
               </div>


      </div> 

     <FormInput description="Email" placeholder="Enter Email" type="email"/>

     <FormButton title="Log in"/>
   </div>

      </div>
    )
  }
}

const FormHeader = props => (
    <h2 id="headerTitle">{props.title}</h2>
);



const FormButton = props => (
  <div id="button" class="row">
    <button>{props.title}</button>
  </div>
);

const FormInput = props => (
  <div class="row">
    <label>{props.description}</label>
    <input type={props.type} placeholder={props.placeholder}/>
  </div>  
);


function App() {
  return (
    <div className="App">
      <header >
        <LoginForm/>
      </header>
    </div>
  );
}

export default App;
