import React from 'react';
import logo from './logo.svg';
import './App.css';
const inputs=[
{ type:'Phone',
regex:/^\d+$/,
length:10 },
{
  type:'Email',
  regex:/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/
}
]

class LoginForm extends React.Component{

  constructor() {
    super();
    this.state = {
      selectedOption: 'Email',
      name:'',
      searchBy:'',
      errors:''
    };
  this.radioChange = this.radioChange.bind(this);
  this.nameChange = this.nameChange.bind(this);
  this.searchByChange = this.searchByChange.bind(this);
}

radioChange(e) {

  this.setState({
    selectedOption: e.currentTarget.value
  });
}
nameChange(e){

  this.setState({
    name: e.currentTarget.value
  });
}
searchByChange(e){
  let  regex=inputs[0].regex;
  const value =e.currentTarget.value && e.currentTarget.value.trim();
  if(!regex.test(value) || value.length===10){
 this.setState({
  errors: {phoneError:true}
});
return ;
}

  this.setState({
    searchBy: e.currentTarget.value
  });
}



  render(){
    const options=['Email','Phone'];
    console.log(this.state)
    return(
      <div id="loginform">
        <FormHeader title="Sample Form" />
        <div>
     <FormInput description="Name" placeholder="Enter  Name" type="text"  onChange={this.nameChange}/>

    <div style={{color:'rgba(187,187,187,0.9)',paddingTop:'2rem',textAlign: 'left',
    paddingLeft: '4.5rem',
    paddingBottom: '0.5rem'}} >Search By</div>
     <div style={{display:'flex',color:'black',justifyContent:'space-around',flexWrap:'wrap'}}>
       {options.map((option,key)=>{ return  <div>
        <input type="radio"
               value={option}
               checked={this.state.selectedOption === option}
               onChange={this.radioChange} />
               {option}
               </div> })}

      </div> 

     <FormInput description="Email" placeholder="Enter Email" type="email" onChange={this.searchByChange}/>

     <FormButton title="Search"/>
   </div>

      </div>
    )
  }
}

const FormHeader = props => (
    <h2 id="headerTitle">{props.title}</h2>
);



const FormButton = props => (
  <div id="button" class="row">
    <button>{props.title}</button>
  </div>
);

const FormInput = props => (
  <div class="row">
    <label>{props.description}</label>
    <input type={props.type} placeholder={props.placeholder} onChange={props.onChange}/>
  </div>  
);


function App() {
  return (
    <div className="App">
      <header >
        <LoginForm/>
      </header>
    </div>
  );
}

export default App;
